<?phpclass Trade{	public $trade_value;	public $trade_amount;	public $trade_type;	public $trade_id = 0;	public $trade_to;	public $trade_from;	public $trade_fees;	public $trade_owner;	public $trade_total;	public $etrade_owner;	public $etrade_type;	public $etrade_id = 0;	public $etrade_value;	public $etrade_amount;	public $standard;	public $standarde;	public function UpdateTrade()	{		$id = $this->trade_id;		$amount = $this->trade_amount;		if($id == 0 && $amount > 0)		{			$trade_to = $this->trade_to;			$trade_from = $this->trade_from;			$trade_value = $this->trade_value;			$trade_owner = $this->trade_owner;			$trade_type = $this->trade_type;			$trade_fees = $this->trade_fees;			$trade_total = $this->trade_total;			mysql_query("INSERT INTO trades (`To`,`From`,`Amount`,`Value`,`User_ID`,`Type`,`Fee`,`Total`)VALUES ('$trade_to','$trade_from','$amount','$trade_value','$trade_owner','$trade_type','$trade_fees','$trade_total');");		}		else		{			if($this->trade_amount == 0)			{							mysql_query("DELETE FROM trades WHERE `Id`='$id'");			}			else			{				mysql_query("UPDATE trades SET `Amount`='$amount' WHERE `Id`='$id'");			}		}			}	public function UpdateETrade()	{		$id = $this->etrade_id;		if($id != 0)		{		$amount = $this->etrade_amount;		if($this->etrade_amount == 0)		{					mysql_query("DELETE FROM trades WHERE `Id`='$id'");		}		else		{			mysql_query("UPDATE trades SET `Amount`='$amount' WHERE `Id`='$id'");		}		}	}	function Trade($id)	{		$tradesql = mysql_query("SELECT * FROM trades WHERE `Id`='$id'");		$this->trade_value = mysql_result($tradesql,0,"Value");		$this->trade_amount = mysql_result($tradesql,0,"Amount");		$this->standard = mysql_result($tradesql,0,"Amount");		$this->trade_id = $id;		$this->trade_from = mysql_result($tradesql,0,"From");		$this->trade_to = mysql_result($tradesql,0,"To");		$this->trade_owner = mysql_result($tradesql,0,"User_ID");		$temp = mysql_result($tradesql,0,"Type");		$sqltemp = mysql_query("SELECT * FROM Wallets WHERE `Acronymn`='$temp'");		$this->trade_type = mysql_result($sqltemp,0,"Id");	}	public function GetEquivalentTrade()	{		$from = $this->trade_from;		$value = $this->trade_value;		$tradesql2 = mysql_query("SELECT * FROM trades WHERE `To` = '$from' AND `Value`=$value");		$this->etrade_id = mysql_result($tradesql2,0,"Id");		$this->etrade_value = mysql_result($tradesql2,0,"Value");		$this->etrade_amount = mysql_result($tradesql2,0,"Amount");		$this->standarde = mysql_result($tradesql2,0,"Amount");		$this->etrade_owner = mysql_result($tradesql2,0,"User_ID");		$this->etrade_to = mysql_result($tradesql2,0,"To");		$this->etrade_from = mysql_result($tradesql2,0,"From");		$temp = mysql_result($tradesql2,0,"To");		$sqltemp = mysql_query("SELECT * FROM Wallets WHERE `Acronymn`='$temp'");		$this->etrade_type = mysql_result($sqltemp,0,"Id");	}	public function ExecuteTrade()	{		if($this->etrade_id != NULL)		{						if($this->trade_from == $this->trade_type)			{				$difference = $this->trade_amount - $this->etrade_amount;				if($difference < 0)				{					$newamount = 0 - $difference;					$sellmoney = $this->trade_value * $this->trade_amount;						$buymoney = ($this->etrade_amount - $newamount);					AddMoney($sellmoney, $this->trade_owner, $this->trade_to);					AddMoney($buymoney,$this->etrade_owner,$this->etrade_to);					$this->etrade_amount = $newamount;					$this->trade_amount = 0;					//echo "1";									}				if($difference > 0)				{					$newamount = 0 + $difference;					$sellmoney = ($this->trade_amount - $newamount) * $this->trade_value;					AddMoney($sellmoney, $this->trade_owner, $this->trade_to);					AddMoney($this->etrade_amount,$this->etrade_owner,$this->etrade_to);					$this->trade_amount = $newamount;					$this->etrade_amount=0;				//	echo "2";				}				if($difference == 0)				{					AddMoney($this->trade_amount * $this->trade_value, $this->trade_owner, $this->etrade_to);					Add