<?phprequire_once("models/config.php");if(!isUserLoggedIn()){echo '<meta http-equiv="refresh" content="0; URL=access_denied.php">';die();}if(!isUserMod($id) and !isUserAdmin($id)){echo '<meta http-equiv="refresh" content="0; URL=access_denied.php">';die();}$account = $loggedInUser->display_username;?>	<h1>Ban A user from chat</h1><form action="" name="banform" method="POST">	<input type="text" name="ban" class="field" />	<input type="submit" value="ban" class="blues" /></form>	<?phpif (isset($_POST["ban"])) {	$banby = $account;	$username = mysql_real_escape_string(strip_tags($_POST["ban"]));	mysql_query("UPDATE userCake_Users SET `ChatBanned`='1' WHERE `Username`='$username'");	mysql_query("UPDATE userCake_Users SET `BannedBy`='$banby' WHERE `Username`='$username'");	$message_sys = mysql_real_escape_string(strip_tags("".$username." was banned from chat."));	$color_sys = mysql_real_escape_string(strip_tags("#FF0000"));	$user_sys = mysql_real_escape_string(strip_tags("system"));	mysql_query("INSERT INTO messages (color, username, message) VALUES ('$color_sys','$user_sys','$message_sys')");}if (isset($_GET["unban"])) {	$username = mysql_real_escape_string(strip_tags($_GET["unban"]));	mysql_query("UPDATE userCake_Users SET `ChatBanned`='0' WHERE `Username`='$username'");	mysql_query("UPDATE userCake_Users SET `BannedBy`=NULL WHERE `Username`='$username'");	echo '<meta http-equiv="refresh" content="0; URL=index.php?page=moderate">';}		?><b>Users Banned From Chat</b><table id="page">	<tr>		<th>Username</th> 		<th>Email</th> 		<th>Role</th>		<th>Status</th>		<th>UnBan</th>	<tr><?php$sqlzz = mysql_query("SELECT * FROM userCake_Users WHERE `ChatBanned`='1'");while ($row = mysql_fetch_assoc($sqlzz)) {$group_id = $row["Group_ID"];$sqls = mysql_query("SELECT * FROM userCake_Groups WHERE `Group_ID`='$group_id'");$group = mysql_result($sqls,0,"Group_Name");$username = $row["Username"];echo'	<tr>		<td>'.$row["Username"].'</td>		<td>'.$row["Email"].'</td>		<td><p>'.$group.'</p></td>		<td>banned by '.$row["BannedBy"].'</td>		<td><a href="index.php?page=moderate&unban='.$row["Username"] . '">Unban</a>	</tr>';	}echo'</table>';	?>